var app = angular.module('h5_angularjs', ['ngRoute']);
app.config(["$routeProvider",function($routeProvider) {
  $routeProvider
    .when('/', {
      templateUrl: 'static/app/view/homepage.html',
      controller: 'homepage'
    })
    .when('/next', {
      templateUrl: 'static/app/view/next.html',
      controller: 'next'
    })
    .when('/form', {
      templateUrl: 'static/app/view/form.html',
      controller: 'form'
    })
    .when('/day/:id', {
      templateUrl: 'static/app/view/day.html',
      controller: 'DayCtrl'
    })
    .otherwise({
      redirectTo: '/'
    });
  }
]);
app.controller("form", ["$scope",function($scope) {
        $scope.form = {
            firstName: "form"
        };
    }
]);
app.controller("homepage", ["$scope",function($scope) {
        $scope.person = {
            firstName: "John",
            lastName: "Doe"
        };
    }
]);
app.controller("next", ["$scope","$location",'$http',function($scope,$location,$http) {
        $scope.next = {
            firstName: "next"
        };
        $scope.count = 0;
        $scope.name = "ZhangLitao";
        $scope.showName = function(){
        	$http({
                method: 'post',
                url: '/User/getUserInfo',
                data: {
                    id: '9527'
                }
            }).success(function(res){
                if(res.resultCode == '000000'){
                    $scope.httpSuc = true;
                    $scope.userInfo = res.result;
                }else{
                    console.log(res);
                }
            }).error(function(res){
                $scope.httpSuc = false;
                alert('发生错误');
            });
            // $location.path("/").replace()
        	// $scope.count++;
        	// console.log( $scope.name.toLocaleLowerCase() );
        };
    }
]);